package conditionals;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int day=8;
		switch(day)
		
		{
		case 1:System.out.print("Day 1");break;
		case 2:System.out.print("Day 2");break;
		case 3:System.out.print("Day 3");break;
		case 4:System.out.print("Day 4");break;
		case 5:System.out.print("Day 5");break;
		default:System.out.println("Invalid input");
		}

	}

}
